#include <iostream>
using namespace std;
int main()
{
    int n, a, b, cnt=0;
    cin >> n;
    for(a=1; a<=n; a++)
    {
        for(b=a; b<=n; b++)
        {
            if(a*b%2==0) cnt++;
        }
    }
    cout << cnt <<endl;
    return 0;
}

#include <iostream>
using namespace std;
int main()
{
    int l, r, a, b, n, cnt = 0;
    cin >> l >> r;
    a = 1;
    while(a<=r)
    {
        b = a;
        while(b<=r)
        {
            n = a+b;
            if(n>=l && n<=r) cnt++;
            b *= 2;
        }
        a *= 2;
    }
cout << cnt <<endl;
return 0;
}

#include <bits/stdc++.h>
using namespace std;
int n, m;
int main() {
    scanf("%d%d", &n, &m);
    assert(1 <= n && n <= 50 && 1 <= m && m <= 50);
    for (int i = 1; i <= n; i++)
        for (int j = 1; j <= m; j++)
            printf("%d%c", i * j, " \n"[j == m]);
    return 0;
}

#include<bits/stdc++.h>
using namespace std;

bool runnian(int y){
	return (y % 4 == 0 && y % 100 != 0) || (y % 400 == 0);
}

int yuefen(int y,int m){
	int day[]={31,28,31,30,31,30,31,31,30,31,30,31};
	if(m==2&&runnian(y)){
		return 29;
	}
	return day[m-1];
}

int main(){
	int y,m,d,h,k;
	int shijian;
	cin>>y>>m>>d>>h>>k;
	
	h+=k;
	
	int d2=h/24;
	h%=24;
	
	while(d2>0){
		int month=yuefen(y,m);
		if(d+d2>month){
			d2-=(month-d+1);
			d=1;
			m++;
			if(m>12){
				m=1;
				y++;
			}
		}else{
			d+=d2;
			d2=0;
		}
	}
	
	cout<<y<<" "<<m<<" "<<d<<" "<<h;
return 0;
}



