#include <iostream>
#include <cmath>
using namespace std;
int main() {
    int t;
    cin >> t;
    while (t--) {
        int a;
        cin >> a;
        int b = (int)(sqrt(sqrt(a)));
        if (b * b * b * b == a) {
            cout << b << endl;
        } else {
            cout << -1 << endl;
        }
    }
    return 0;
}

#include <bits/stdc++.h>
using namespace std;
#define ll long long
int main() {
    int n;
    cin >> n;
    int x = 0;
    for (int i = 0; i < n; i++) {
        int smu = 0;
        ll tmp;
        cin >> tmp;
        while (tmp > 0) {
            smu += tmp % 10;
            tmp /= 10;
        }
        x = max(x, smu);
    }
    cout << x << endl;
    return 0;
}

