#include<bits/stdc++.h>
using namespace std;
bool check(int x){
int y = sqrt(x);
return y*y==x;
}
int main(){
int t;
cin>>t;
while(t--){
int n;
cin>>n;
int fl=0;
for(int i=1;i*i<n;i++){
int j=n-i*i;
if(check(j))fl=1;
}
if(fl)cout<<"Yes\n";
else cout<<"No\n";
}
}

#include <iostream>
using namespace std;
int check(int x, int y) {
int cnt = 0;
while (x > 0) {
int tmp = x % 10;
if (tmp == y) {
cnt++;
}
x = x / 10;
}
return cnt;
}
int main() {
int n, k;
cin >> n >> k;
int ans = 0;
for (int i = 1; i <= n; i++) {
ans += check(i, k);
}
cout << ans << endl;
return 0;
}

