#include <iostream>
using namespace std;
int main() {
int n;
cin >> n;
for (int i = 0; i < n; ++i) {
for (int j = 0; j < n; ++j) {
char ch;
if (j == 0 || j == n - 1) {
ch = '|';
} else if (i == 0 || i == n - 1 || i == n / 2) {
ch = '-';
} else {
ch = 'x';
}
cout << ch;
}
cout << endl;
}
return 0;
}

#include <stdio.h>
int main(int argc, char **argv)
{
long long a=0, b=0, m=0, n=0, ans=0, c=0, i;
scanf("%lld%lld%lld%lld", &a, &b, &m, &n);
ans = a+b;
for(i=3; i<=n; i++)
{
c = a+b;
ans += c;
a = b;
b = c;
if(c>=m) break;
}
printf("%lld\n", ans);
return 0;
}

#include<bits/stdc++.h>
using namespace std;
int main() {
	int N;
	cin>>N;
	for(int i=1;i<=N;i++){
		for(int j=1;j<=N;j++){
			if(j==1 || j==N) cout<<'|';
			else if((j!=1 && j!=N) && i==(N+1)/2) cout<<'-';
			else cout<<'a';
		}
		cout<<endl;
	}
	return 0;
}

