#include <iostream>
using namespace std;
int main() {
int n = 0;
cin >> n;
 for (int t = 0; ; t++) {
 if (n == 495) {
 cout << t << endl;
 break;
 }
 int m0 = n % 10, m1 = n / 10 % 10, m2 = n / 100;
 int tmax = 0, tmin = 0;
 if (m0 >= m1 && m1 >= m2) {
 tmax = m0 * 100 + m1 * 10 + m2;
 tmin = m2 * 100 + m1 * 10 + m0;
 } else if (m0 >= m2 && m2 >= m1) {
 tmax = m0 * 100 + m2 * 10 + m1;
 tmin = m1 * 100 + m2 * 10 + m0;
 } else if (m1 >= m0 && m0 >= m2) {
 tmax = m1 * 100 + m0 * 10 + m2;
 tmin = m2 * 100 + m0 * 10 + m1;
 } else if (m1 >= m2 && m2 >= m0) {
 tmax = m1 * 100 + m2 * 10 + m0;
 tmin = m0 * 100 + m2 * 10 + m1;
 } else if (m2 >= m0 && m0 >= m1) {
 tmax = m2 * 100 + m0 * 10 + m1;
 tmin = m1 * 100 + m0 * 10 + m2;
 } else { // m2 >= m1 && m1 >= m0
 tmax = m2 * 100 + m1 * 10 + m0;
 tmin = m0 * 100 + m1 * 10 + m2;
 }
 n = tmax - tmin;
 }
return 0;
}

#include <iostream>
using namespace std;
int main() {
int a = 0, b = 0, cnt = 0;
cin >> a >> b;
for (int n = a; n <= b; n++) {
bool isPrime = true;
for (int i = 2; i * i <= n; i++) {
if (n % i == 0) {
isPrime = false;
break;
}
}
if (isPrime)
cnt++;
}
cout << cnt << endl;
return 0;
}

#include <iostream>
using namespace std;
int main() {
int m = 0;
cin >> m;
for (int i = 0; i < m; i++) {
int n = 0;
cin >> n;
// 数一下 n 有多少位数，记为 l
int t = n, l = 0;
while (t > 0) {
t /= 10;
l++;
}
// 每位数 l 次方求和，记为 sum
int sum = 0;
t = n;
while (t > 0) {
int d = t % 10;
t /= 10;
int mul = 1;
for (int j = 0; j < l; j++)
mul *= d;
sum += mul;
}
// 根据 sum 和 n 是否相等，判断是否为自幂数
if (sum == n)
cout << "T" << endl;
else
cout << "F" << endl;
}
return 0;
}

