}

#include <iostream>
using namespace std;
int main() {
 int x, y, z, n, m, cnt = 0;
 cin >> x >> y >> z >> n >> m;
 for (int gj = 0; gj * x <= n && gj <= m; gj++)
 for (int mj = 0; mj * y + gj * x <= n && mj + gj <= m; mj++) {
 int xj = (n - gj * x - mj * y) * z;
 if (gj + mj + xj == m)
 cnt++;
 }
 cout << cnt << endl;
 return 0;
}

#include <algorithm>
#include <cstdio>
using namespace std;
int main() {
	int n, i, j, k;
	scanf("%d", &n);
	k = n / 2;
	for (i = 1; i <= n; i++) {
	for (j = 1; j <= n; j++) {
	if (abs(k - i + 1) + abs(k - j + 1) == k)
	printf("#");
	else
	printf(".");
	}
	printf("\n");
	}
	return 0;
}

#include <algorithm>
#include <cstdio>
using namespace std;
int n, ans;
int main() {
	scanf("%d", &n);
	for (int i = 1; i <= n; i++) {
	int v = i % 10, t = i / 10, chk = 1;
	while (t) {
		if (t % 10 != v) chk = 0;
			t /= 10;
		}
		ans += chk;
	}
	printf("%d\n", ans);
	return 0;
}
