int main(){
	int n;
	cin>>n;
	for(int i1=0;i1<n;i1++){
	int a;
	string a1,b1,aaaa;
	cin>>a>>a1>>aaaa>>aaaa>>b1;
	int i=a;
	if(a1=="m") a*=1000;
	else if(a1=="km"){
		if(b1=="m")a*=1000;
		else a*=1000000;
	}
	else if(a1=="g") a*=1000;
	else if(a1=="kg"){
		if(b1=="g")a*=1000;
		else a*=1000000;
	}	
	cout<<i<<' '<<a1<<" = "<<a<<' '<<b1<<endl;
	}
}

#include <bits/stdc++.h>
using namespace std;

int aaa(int a){
	int cnt=0;
	while(1){
		if(a%2) cnt++;
		a/=2;
		if(a==0) return cnt;
	}
}

int main(){
    int n,cnt=0;
    cin>>n;
    for(int i=0;i<n;i++){
		int a;
		cin>>a;
		cnt+=aaa(a);
	}
	cout<<cnt<<' ';
	if(cnt%2) cout<<1;
	else cout<<0; 
}

