#include <bits/stdc++.h>
using namespace std;

void zxx(string &a){
	for(int i=0;i<a.size();++i){
		if(a[i]<'a') a[i]+='a'-'A';
	}
} 

int main(){
    int x;
    cin>>x;
    string srr[x];
    int arr[x]={0};
    for(int y=1;y<=x;y++){
    	string a;
		cin>>a;
		zxx(a);
		for(int i=0;i<x;i++){
			if(a==srr[i]) arr[i]++;
			else if(srr[i]==""){
				srr[i]=a;
				arr[i]++;
			}
		}
	}int m=-1,j=0;
	for(int i=0;i<x;i++){
		if(arr[i]>m){
			m=arr[i];
			j=i;
		}
	}
	cout<<srr[j];
}

#include <bits/stdc++.h>
