int main(){
    int n,k,mi=100000,ma=-100000;
    cin>>n>>k;
    int arr[n]={0};
    for(int i=0;i<n;++i){
		cin>>arr[i];
		if(arr[i]<mi) mi=arr[i];
		else if(arr[i]>ma) ma=arr[i];
	}
	for(int i=0;i<n;++i){
		if(arr[i]<k) cout<<mi;
		else if(arr[i]>k) cout<<ma;
		else cout<<arr[i];
		cout<<' ';
	}
}

#include <bits/stdc++.h>
using namespace std;

int main(){
    int n;
    cin>>n;
    for(int i=0;i<n;i++){
		int n1,f=1;
		cin>>n1;
		int arr[n1]={0};
		for(int j=0;j<n1;j++){
			cin>>arr[j];
		}
		for(int j=0;j<n1;j++){
			int sum1=0,sum2=0;
			for(int k=0;k<n1;k++){
				if(k<=j) sum1+=arr[k];
				else sum2+=arr[k];
			}
			if(sum1==sum2){
				cout<<"Yes"<<endl;
				f=0;
				break;
			}
		}
		if(f){
			cout<<"No";
		}
	}
}

#include <bits/stdc++.h>
using namespace std;

bool aaa(string a){
	int aa=a.size()/2;
	if(a.size()<2) return 0;
	for(int i=0;i<aa;i++){
		if(a[i]!=a[a.size()-i-1]) return 0;
	}return 1;
}

int main(){
    int n;
    cin>>n;
    for(int i=0;i<n;i++){
		string a;int f=0;
		cin>>a;
		for(int j=0;j<=a.size()/2;j++){
			string b=a.substr(0,j);
			if(aaa(b)){
				b=a.substr(a.size()-j,j);
				if(aaa(b)) f=1;
			}
		}
		if(f) cout<<"Yes"<<endl;
		else cout<<"No"<<endl;
	}
}

