#include <bits/stdc++.h>
using namespace std;

int main(){
    int t;
    cin>>t;
    for(int i=0;i<t;++i){
		int n,a=1;
		cin>>n;
		int arr[n]={0};
		for(int j=0;j<n;++j){
			cin>>arr[j];
		}
		for(int j=0;j<n;++j){
			int f=1;
			for(int k=0;k<n;++k){
				if(arr[j]%arr[k]!=0) f=0;
			}
			if(f){
				cout<<"Yes"<<endl;
				a=0;
				break;
			}
		}
		if(a) cout<<"No"<<endl;
	}
}

#include <bits/stdc++.h>
