int main(){
    int n,cnt=0;
    string a;
	cin>>n>>a;
    for(int i=0;i<n;i++){
		if(a[i]>='a') cnt=cnt+a[i]-'a'+1;
		else cnt-=a[i];
	}cout<<cnt;
}

#include <bits/stdc++.h>
using namespace std;

int main(){
    int n,cnt=0;
    cin>>n;
    int arr[n]={0};
    for(int i=0;i<n;i++){
		cin>>arr[i];
	}
	for(int i=0;i<n;i++){
		for(int j=i;j<n;j++){
			int a=arr[i]+arr[j];
			if(sqrt(a)*sqrt(a)==a) cnt++;
		}
	}cout<<cnt;
}

