#include<bits/stdc++.h>
using namespace std;
typedef long long LL;
int main() {
	string m;
	getline(cin, m);
	vector<string> arry;
	string j;
	for (char k : m) {
		if (k == ',') {
			if (!j.empty()) {
				arry.push_back(j);
				j.clear();
			}
		} else {
			j += k;
		}
	}
	if (!j.empty()) {
		arry.push_back(j);
	}
	for (string &y : arry) {
		LL l = y.length();
		if (l < 6 || l > 12) {
			continue;
		}
		bool u = false, i = false, o = false;
		bool p = false;
		bool n = true;
		for (char k : y) {
			if (k >= 'a' && k <= 'z') {
				u = true;
			} else if (k >= 'A' && k <= 'Z') {
				i = true;
			} else if (k >= '0' && k <= '9') {
				o = true;
			} else if (k == '!' || k == '@' || k == '#' || k == '$') {
				p = true;
			} else {
				n = false;
				break;
			}
		}
		LL h = 0;
		if (u == true) {
			h++;
		}
		if (i == true) {
			h++;
		}
		if (o == true) {
			h++;
		}
		if (p && n && h >= 2) {
			cout << y << endl;
		}
	}
	return 0;
}

#include <algorithm>
#include <cstdio>
using namespace std;
const int N = 105;
int n;
int a[N];
int cnt;
int main() {
	scanf("%d", &n);
	for (int i = 1; i <= n; i++) scanf("%d", &a[i]);
	while (1) {
	int mx = n;
	for (int i = 1; i <= n; i++)
	if (a[i] >= a[mx]) mx = i;
	if (a[mx] == 0) break;
	int mn = a[mx];
	for (int i = 1; i <= n; i++)
	if (a[i] > 0) mn = min(mn, a[i]);
	a[mx] -= mn;
	cnt++;
	}
	printf("%d\n", cnt);
	return 0;
}

#include <algorithm>
#include <cstdio>

using namespace std;

int days[20] = {0, 31, 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31};

int main() {
    int m;
    scanf("%d", &m);
    printf("MON TUE WED THU FRI SAT SUN\n");
    int d = days[m];

    int w = 1;
    if (m > 9) {
        for (int i = 9; i < m; i++) w = (w + days[i] - 1) % 7 + 1;
    } else if (m < 9) {
        for (int i = 8; i >= m; i--) w = ((w - days[i]) % 7 + 7) % 7;
        if (w == 0) w = 7;
    }

    for (int i = 1; i < w; i++) printf("   ");
    for (int i = 1; i <= d; i++) {
        printf("% 3d", i);
        w = w % 7 + 1;
        if (w == 1 || i == d)
            printf("\n");
        else
            printf(" ");
    }
    return 0;
}
